//引入Count的UI组件
import CountUI from '../../components/Count'
import { createDecrementAction } from '../../redux/count_action'

//引入connect用于连接UI组件与redux
import { connect } from 'react-redux'

//函数返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value——状态
function mapStateToProps(state) {
    return {
        count: state
    }
}


//函数返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value——操作状态的方法
function b(dispatch) {
    return {
        jia: (number) => {
            //通知redux执行加法
            dispatch(createDecrementAction(number))
        }
    }
}




//使用connect()()创建并暴露一个Count的容器组件
export default connect(mapStateToProps, b)(CountUI)
