import React, { useState } from 'react';
import './FileUpload.css';

function FileUpload() {
    const [taskId, setTaskId] = useState(null);
    const [status, setStatus] = useState(null);
    const [downloadLink, setDownloadLink] = useState(null);

    const uploadFile = async (event) => {
        event.preventDefault();
        const fileInput = document.getElementById('fileInput');
        const file = fileInput.files[0];
        if (!file) {
            alert('Please select a file.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        const response = await fetch('/api/upload', {
            method: 'POST',
            body: formData
        });

        const result = await response.json();
        setTaskId(result.taskId);

        // 开始轮询检查文件处理状态
        checkStatus(result.taskId);
    };

    const checkStatus = async (taskId) => {
        const intervalId = setInterval(async () => {
            const response = await fetch(`/api/status/${taskId}`);
            const result = await response.json();
            setStatus(result.status);

            if (result.status === 'completed') {
                clearInterval(intervalId);
                alert('File processing completed. You can now download the result.');
                setDownloadLink(`/api/download/${taskId}`);
            }
        }, 60000); // 每隔60秒检查一次状态
    };

    return (
        <div className="file-upload-container">
            <form id="uploadForm" onSubmit={uploadFile} encType="multipart/form-data" className="upload-form">
                <div className="upload-box">
                    <input type="file" id="fileInput" accept=".csv, .xls, .xlsx" className="file-input" />
                    <div className="upload-placeholder">
                        <div className="upload-icon">⬆️</div>
                        <div className="upload-text">点击上传或拖动文件到此</div>
                    </div>
                </div>
                <div className="button-container">
                    <button type="submit" className="upload-button">上传</button>
                    <button type="button" className="cancel-button">取消</button>
                </div>
            </form>
            {status && <p className="status-text">Status: {status}</p>}
            {downloadLink && (
                <a id="downloadLink" href={downloadLink} download="result.xlsx" className="download-link">下载处理文件</a>
            )}
        </div>
    );
}

export default FileUpload;
